    def validSequence(self, w1: str, w2: str) -> List[int]:
        # w1.sort()
        # w2.sort()
        n, m = len(w1), len(w2)
        chardict_ = defaultdict(list)

        for i, c in enumerate(w1):
            chardict_[c].append(i)

        cache = {}

        def dfs(p, li, cng):
            if p == m:
                return True, []

            test = (p, li, cng)
            if test in cache:
                return cache[test]

            t = w2[p]
            maybe = []

            if t in chardict_:
                idx_list = chardict_[t]
                i = bisect_right(idx_list, li)
                if i < len(idx_list):
                    next_idx = idx_list[i]
                    good, seq = dfs(p + 1, next_idx, cng)
                    if good:
                        maybe.append([next_idx] + seq)

            if not cng:
                start = li + 1
                for chidx in range(start, n):
                    good, seq = dfs(p + 1, chidx, True)
                    if good:
                        maybe.append([chidx] + seq)
                        break  
            if maybe:
                smol = min(maybe)
                cache[test] = (True, smol)
                return cache[test]

            cache[test] = (False, [])
            return cache[test]

        @lru_cache
        def helper(x):
            res = 5
            for s in x:
                res += s
            return res

        good, ans = dfs(0, -1, False)

